import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdministrativosDashboard = () => {
  // Keep the same states
  const [users, setUsers] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [error, setError] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [showCreateUser, setShowCreateUser] = useState(false);
  const [userFormData, setUserFormData] = useState({
    name: '',
    email: '',
    role: '',
    company_id: ''
  });

  const navigate = useNavigate();

  // Make fetchData a useCallback to fix the dependency warning
  const fetchData = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      const [usersRes, companiesRes, vehiclesRes, routesRes, currentUserRes] = await Promise.all([
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/users/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/companies/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/vehicles/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/routes/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/users/me`, { headers })
      ]);

      // Filter out ADMIN and ADMINISTRATIVO users
      const filteredUsers = usersRes.data.filter(user => 
        !['ADMIN', 'ADMINISTRATIVO'].includes(user.role)
      );

      setUsers(filteredUsers);
      setCompanies(companiesRes.data);
      setVehicles(vehiclesRes.data);
      setRoutes(routesRes.data);
      setCurrentUser(currentUserRes.data);
    } catch (err) {
      if (err.response?.status === 401) {
        navigate('/');
      }
      setError('Error al cargar datos');
    }
  }, [navigate]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleSignOut = async () => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/logout`, {});
      localStorage.clear();
      sessionStorage.clear();
      if (response.data.logoutUrl) {
        window.location.replace(response.data.logoutUrl);
      } else {
        window.location.replace('/');
      }
    } catch (error) {
      console.warn('Logout error:', error);
      localStorage.clear();
      sessionStorage.clear();
      window.location.replace('/');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Dashboard Administrativo</h1>
        <div className="header-buttons">
          <button onClick={handleSignOut} className="sign-out-button">
            Sign Out
          </button>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="dashboard-content">
        {/* Companies Section */}
        <section className="companies-section">
          <h2>Empresas de Transporte</h2>
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>NIT</th>
                <th>Email</th>
                <th>Teléfono</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {companies.map(company => (
                <tr key={company.id}>
                  <td>{company.name}</td>
                  <td>{company.nit}</td>
                  <td>{company.email}</td>
                  <td>{company.phone}</td>
                  <td>{company.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Users Section */}
        <section className="users-section">
          <h2>Usuarios</h2>
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Email</th>
                <th>Rol</th>
                <th>Estado</th>
                <th>Empresa</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.role}</td>
                  <td>{user.status}</td>
                  <td>{companies.find(c => c.id === user.company_id)?.name || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Vehicles Section */}
        <section className="vehicles-section">
          <h2>Vehículos</h2>
          <table>
            <thead>
              <tr>
                <th>Marca</th>
                <th>Modelo</th>
                <th>Año</th>
                <th>Tipo</th>
                <th>Placa</th>
                <th>Número</th>
                <th>Empresa</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {vehicles.map(vehicle => (
                <tr key={vehicle.id}>
                  <td>{vehicle.brand}</td>
                  <td>{vehicle.model}</td>
                  <td>{vehicle.year}</td>
                  <td>{vehicle.vehicle_type}</td>
                  <td>{vehicle.plate_number}</td>
                  <td>{vehicle.company_number}</td>
                  <td>{companies.find(c => c.id === vehicle.company_id)?.name || '-'}</td>
                  <td>{vehicle.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>

        {/* Routes Section */}
        <section className="routes-section">
          <h2>Rutas</h2>
          <table>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Origen</th>
                <th>Destino</th>
                <th>Salida</th>
                <th>Duración</th>
                <th>Empresa</th>
                <th>Vehículo</th>
                <th>Estado</th>
              </tr>
            </thead>
            <tbody>
              {routes.map(route => (
                <tr key={route.id}>
                  <td>{route.name}</td>
                  <td>{route.start_point}</td>
                  <td>{route.end_point}</td>
                  <td>{new Date(route.departure_time).toLocaleString()}</td>
                  <td>{route.estimated_duration} min</td>
                  <td>{companies.find(c => c.id === route.company_id)?.name || '-'}</td>
                  <td>{vehicles.find(v => v.id === route.vehicle_id)?.plate_number || '-'}</td>
                  <td>{route.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </section>
      </div>
    </div>
  );
};

export default AdministrativosDashboard;
