import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdministrativosDashboard = () => {
  // States
  const [users, setUsers] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [error, setError] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const [showCreateUser, setShowCreateUser] = useState(false);
  const [userFormData, setUserFormData] = useState({
    name: '',
    email: '',
    role: '',
    company_id: ''
  });
  const navigate = useNavigate();

  // Fetch functions
  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`
      };

      // Fetch all data in parallel
      const [usersRes, companiesRes, vehiclesRes, routesRes, currentUserRes] = await Promise.all([
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/users/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/companies/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/vehicles/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/routes/`, { headers }),
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/users/me`, { headers })
      ]);

      // Filter out ADMIN and ADMINISTRATIVO users
      const filteredUsers = usersRes.data.filter(user => 
        !['ADMIN', 'ADMINISTRATIVO'].includes(user.role)
      );

      setUsers(filteredUsers);
      setCompanies(companiesRes.data);
      setVehicles(vehiclesRes.data);
      setRoutes(routesRes.data);
      setCurrentUser(currentUserRes.data);
    } catch (err) {
      if (err.response?.status === 401) {
        navigate('/');
      }
      setError('Error al cargar datos');
    }
  };

  useEffect(() => {
    fetchData();
  }, [navigate]);

  const handleSignOut = async () => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_BACKEND_URL}/logout`, {});
      localStorage.clear();
      sessionStorage.clear();
      if (response.data.logoutUrl) {
        window.location.replace(response.data.logoutUrl);
      } else {
        window.location.replace('/');
      }
    } catch (error) {
      console.warn('Logout error:', error);
      localStorage.clear();
      sessionStorage.clear();
      window.location.replace('/');
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Dashboard Administrativo</h1>
        <div className="header-buttons">
          <button onClick={handleSignOut} className="sign-out-button">
            Sign Out
          </button>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="dashboard-content">
        {/* Companies Section */}
        {/* Same as AdminDashboard but without create/edit/delete for ADMIN/ADMINISTRATIVO */}
        <section className="companies-section">
          {/* Companies table */}
        </section>

        {/* Users Section */}
        <section className="users-section">
          {/* Users table without ADMIN/ADMINISTRATIVO users */}
        </section>

        {/* Vehicles Section */}
        <section className="vehicles-section">
          {/* Vehicles table */}
        </section>

        {/* Routes Section */}
        <section className="routes-section">
          {/* Routes table */}
        </section>
      </div>
    </div>
  );
};

export default AdministrativosDashboard;
