import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('username', username);
      formData.append('password', password);

      const response = await axios.post('http://localhost:8000/login', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      });

      localStorage.setItem('token', response.data.access_token);
      window.location.href = '/dashboard';
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  const handleCognitoLogin = () => {
    window.location.href = 'http://localhost:8000/login';
  };

  const handleCognitoCallback = async () => {
    const params = new URLSearchParams(window.location.search);
    const code = params.get('code');

    if (code) {
        try {
            const response = await axios.get(`http://localhost:8000/auth?code=${code}`);

            // Store token and user info
            localStorage.setItem('token', response.data.access_token);
            localStorage.setItem('user', JSON.stringify(response.data.user));

            // Redirect to dashboard
            if (response.data.redirect_url) {
                window.location.href = response.data.redirect_url;
            } else {
                window.location.href = '/dashboard';
            }
        } catch (err) {
            setError('Authentication failed');
        }
    }
};

// Call handleCognitoCallback when component mounts
useEffect(() => {
    handleCognitoCallback();
}, []);

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        {error && <div className="error">{error}</div>}
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login with Password</button>
      </form>
      <button onClick={handleCognitoLogin}>Login with Cognito</button>
    </div>
  );
};

export default Login;
